import React, { Component } from 'react'
import axios from 'axios'


 class FormMovie extends Component {

    constructor(props) {
        super(props)

        this.state = {
             movieName :"",
             movieURL: "",
             comment:"",
        }
        this.onChange = this.onChange.bind(this);
        this.submitForm = this.submitForm.bind(this);
    }
//Un champ texte pour saisir le nom du film
//Un champ texte pour saisir une URL correspondant au poster du film
//Un champ textarea permettant de saisir un commentaire


    render() {
        return (
            <div>
                <div className="FormMovie">
                  <h1>Ton film préféré</h1>

                  <form onSubmit={this.submitForm}>
                    <fieldset>
                      <legend>Informations</legend>
                      <div className="form-data">
                        <label htmlFor="movieName">Nom du film</label>
                        <input
                          type="text"
                          id="movieName"
                          name="movieName"
                          onChange={this.onChange}
                          value={this.state.movieName}
                        />
                      </div>

                      <div className="form-data">
                        <label htmlFor="movieURL">URL</label>
                        <input
                          type="text"
                          id="movieURL"
                          name="movieURL"
                          onChange={this.onChange}
                          value={this.state.movieURL}
                        />
                      </div>

                      <div className="form-data">
                        <label htmlFor="email">E-mail</label>
                        <input
                          type="email"
                          id="email"
                          name="email"
                          onChange={this.onChange}
                          value={this.state.email}
                        />
                      </div>
                      <hr />
                      <div className="form-data">
                        <input type="submit" value="Envoyer" />
                      </div>
                    </fieldset>
                  </form>
                </div>

            </div>
        )
    }
}

export default FormMovie
